package offer1;import java.util.Stack;/** * 用两个栈来实现一个队列，完成队列的Push和Pop操作。 队列中的元素为int类型 */public class TestFive {    static Stack<Integer> stack1 = new Stack<Integer>();    static Stack<Integer> stack2 = new Stack<Integer>();    public static void push(int node) {        stack1.push(node);    }    /**     * 要预先判断stack2是否为空     * @return     */    public static int pop() {        if (!stack2.isEmpty()) {            return stack2.pop();        } else {            while (!stack1.isEmpty()) {                stack2.push(stack1.pop());            }        }        return stack2.pop();    }    public static void main(String[] args) {        push(1);        push(2);        push(3);        push(4);        push(5);        for (int i = 0; i < 5; i++) {            System.out.println(pop());        }    }}