package offer1;/** * 变态跳台阶 */public class TestNine {    public static void main(String[] args) {        System.out.println(JumpFloorII2(4));    }    /**     * f(n) = f(0) + .... + f(n - 2) + f(n - 1)     * f(n - 1) = f(0) + ... + f(n - 2)     * 所以f(n) = 2 * f(n - 1)     * @param target     * @return     */    public static int JumpFloorII(int target) {        if (target <= 0) {            return 0;        }        if (target == 1) {            return 1;        }        return 2 * JumpFloorII(target - 1);    }    /**     * 非递归     * @param target     * @return     */    public static int JumpFloorII2(int target) {        int pre = 1;        int result = 0;        if (target <= 0) {            return 0;        }        if (target == 1) {            return pre;        }        for (int i = 2; i <= target; i++) {            result = 2 * pre;            pre = result;        }        return result;    }}