package offer1;/** * 1.二维数组中的查找 */public class TestOne {    public static void main(String[] args) {        int[][] array = {{1,2,3,4},{5,6,7,8},{6,7,8,9},{7,10,11,12}};        Find(4, array);    }    /**     * 从左下角 往上遍历.     * @param target     * @param array     * @return     */    public static boolean Find(int target, int [][] array) {        int row = array.length - 1;        int col = 0;        while (row >= 0 && col < array[row].length) {            if (target == array[row][col]) {                return true;            } else if (target < array[row][col]) {                row--;            } else if (target > array[row][col]) {                col++;            }        }        return false;    }}