package offer1;/** * 斐波那契数列 */public class TestSeven {    public static void main(String[] args) {        System.out.println(Fibonacci(5));    }    /**     * 递归     * @param n     * @return     */    public static int Fibonacci(int n) {        if (n == 0) {            return 0;        }        if (n == 1 || n == 2) {            return 1;        }        return Fibonacci(n - 1) + Fibonacci(n - 2);    }    /**     * 用循环     * @param n     * @return     */    public static int Fibonacci1(int n) {        int prenum = 1;        int preprenum = 0;        int result = 0;        if (n == 0) {            return 0;        }        if (n == 1) {            return 1;        }        for (int i = 2; i <= n; i++) {            result = prenum + preprenum;            preprenum = prenum;            prenum = result;        }        return  result;    }}