package offer1;/** * 矩阵覆盖 (斐波那契) */public class TestTen {    public static void main(String[] args) {    }    /**     * 当target  > 3 ，f(target) =  f(target - 1) + f(target - 2)     * src = https://www.nowcoder.com/questionTerminal/72a5a919508a4251859fb2cfb987a0e6     * @param target     * @return     */    public static int RectCover(int target) {        int prepre = 1;        int pre = 2;        int result = 0;        if (target == 0) {            return 0;        }        if (target == 1) {            return prepre;        }        if (target == 2) {            return pre;        }        for (int i = 3; i <= target; i++) {            result = pre + prepre;            prepre = pre;            pre = result;        }        return result;    }}