package offer1;/** * 2.将字符串中的空格替换为%20 */public class TestTwo {    public static void main(String[] args) {        StringBuffer sb = new StringBuffer("We Are Happy");        String s = replaceSpace(sb);        System.out.println(s);    }    /**     * 新准备一个字符串存储字符     * @param str     * @return     */    public static String replaceSpace(StringBuffer str) {        StringBuffer sb = new StringBuffer();        for (int i = 0; i < str.length(); i++) {            if (str.charAt(i) == ' ') {                sb.append("%20");            } else {                sb.append(str.charAt(i));            }        }        return sb.toString();    }}