package offer2;public class TestFour {    public static void main(String[] args) {        ListNode head = new ListNode(1);        head.next = new ListNode(2);        head.next.next = new ListNode(3);        head.next.next.next = new ListNode(4);        head.next.next.next.next = new ListNode(5);        head.next.next.next.next.next = new ListNode(6);        System.out.println(length(head));        ListNode listNode = FindKthToTail(head, 0);        while (listNode != null) {            System.out.println(listNode.val);            listNode = listNode.next;        }    }    public static int length(ListNode head) {        if (head == null) {            return 0;        }        int lenght = 0;        ListNode node = head;        while (node != null) {            node = node.next;            lenght++;        }        return lenght;    }    public static ListNode FindKthToTail(ListNode head,int k) {        if (head == null) {            return null;        }        if (k > length(head)) {            return null;        }        int flag = k;        ListNode node = head;        ListNode temp = head;        while (flag > 1) {            node = node.next;            flag--;        }        while (node.next != null) {            temp = temp.next;            node = node.next;        }        return temp;    }}class ListNode {    int val;    ListNode next = null;    ListNode (int val) {        this.val = val;    }}