package offer2;/** * 求1的个数。 二进制中 */public class TestOne {    public static void main(String[] args) {        System.out.println(NumberOf2(10));    }    public static int NumberOf1(int n) {        int result = 0;        String s = Integer.toBinaryString(n);        char[] chars = s.toCharArray();        for (char aChar : chars) {            if (aChar == '1') {                result++;            }        }        return result;    }    public static int NumberOf2(int n) {        int result = 0;        long l = Integer.toUnsignedLong(n);        while (n != 0) {            result += n & 1;            n = n >> 1;        }        return result;    }}