package offer2;/** *给定一个double类型的浮点数base和int类型的整数exponent。求base的exponent次方。 */public class TestTwo {    public static void main(String[] args) {        System.out.println(Power(1.1, 2));    }    public static double Power(double base, int exponent) {        double result = base;        if (exponent == 0) {            return 1;        }        if (exponent < 0) {            if (base == 0) {                System.out.println("分母不能为0");            }            exponent = -exponent;            while (exponent > 1) {                result = result * base;                exponent--;            }            return 1/result;        }        while (exponent > 1) {            result = result * base;            exponent--;        }        return result;    }}