package offer3;import java.util.ArrayList;import java.util.Collections;import java.util.HashSet;public class TestSeven {    public static void main(String[] args) {    }    public static ArrayList<String> Permutation(String str) {       ArrayList<String> list = new ArrayList<>();       if (str == null || str.length() == 0) {           return list;       }       HashSet<String> set = new HashSet<>();       fun(set, str.toCharArray(), 0);       list.addAll(set);       Collections.sort(list);       return list;    }    public static void fun(HashSet<String> set, char[] str, int k) {        if (k == str.length) {            set.add(new String(str));            return;        }        for (int i = k; i < str.length; i++) {            swap(str, i, k);            fun(set, str, k + 1);            swap(str, i, k);        }    }    public static void swap(char[] str, int i, int j) {        if (i != j) {            char temp = str[i];            str[i] = str[j];            str[j] = temp;        }    }}