package offer3;import java.util.ArrayList;import java.util.LinkedList;import java.util.Queue;public class TestTwo {    public ArrayList<Integer> PrintFromTopToBottom(TreeNode root) {        if (root == null) {            return null;        }        ArrayList<Integer> list = new ArrayList<>();        Queue<TreeNode> queue = new LinkedList<>();        queue.add(root);        while (!queue.isEmpty()) {            TreeNode node = queue.poll();            if (node.left != null) {                queue.offer(node.left);            }            if (node.right != null) {                queue.offer(node.right);            }            list.add(node.val);        }        return list;    }}class TreeNode {    int val = 0;    TreeNode left = null;    TreeNode right = null;    public TreeNode(int val) {        this.val = val;    }}